	if (typeof wrapper == 'string') { 
		var node = el(wrapper); 		
		if (!node) { throw "Flowplayer cannot access element: " + wrapper; }
		wrapper = node; 
		init();
		
	// we have a DOM element so page is already loaded
	} else {		
		init();
	}
	
	
